$('#collapseButton').on('click', function(e) {
  if($('#collapseButton').hasClass("expanded")){
    $('#collapseButton').text('Rozwiń');
    $('#collapseButton').removeClass("expanded");
  } else {
    $('#collapseButton').text('Zwiń');
    $('#collapseButton').addClass("expanded");
  }
});


